import React, { useState, useEffect } from "react";
import { ethers } from "ethers";
import ABI from "./ValueCentricABI.json";  // paste ABI from Remix

// ðŸ‘‡ Replace with your actual deployed contract address from Remix
const CONTRACT_ADDRESS = "0xYourDeployedAddressHere";

function App() {
  const [account, setAccount] = useState(null);
  const [contract, setContract] = useState(null);

  const [productId, setProductId] = useState("");
  const [serial, setSerial] = useState("");
  const [receiver, setReceiver] = useState("");
  const [docs, setDocs] = useState([]);

  // Connect to MetaMask
  const connectWallet = async () => {
    if (window.ethereum) {
      const provider = new ethers.BrowserProvider(window.ethereum);
      const signer = await provider.getSigner();
      const accounts = await provider.send("eth_requestAccounts", []);
      setAccount(accounts[0]);

      const contractInstance = new ethers.Contract(
        CONTRACT_ADDRESS,
        ABI,
        signer
      );
      setContract(contractInstance);
    } else {
      alert("MetaMask not detected!");
    }
  };

  // Register product (Manufacturer only)
  const registerProduct = async () => {
    if (!contract) return;
    const tx = await contract.registerProduct(productId, serial);
    await tx.wait();
    alert("Product registered!");
  };

  // Transfer product (Distributor or Manufacturer)
  const transferProduct = async () => {
    if (!contract) return;
    const tx = await contract.transferProduct(productId, serial, receiver);
    await tx.wait();
    alert("Product transferred!");
  };

  // Fetch product documents
  const fetchDocs = async () => {
    if (!contract) return;
    const key = await contract.computeProductKey(productId, serial);
    const result = await contract.getDocs(key);
    setDocs(result);
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>ValueCentric Supply Chain DApp</h1>
      {!account ? (
        <button onClick={connectWallet}>Connect MetaMask</button>
      ) : (
        <p>Connected: {account}</p>
      )}

      <h2>Register Product</h2>
      <input
        type="text"
        placeholder="Product ID"
        value={productId}
        onChange={(e) => setProductId(e.target.value)}
      />
      <input
        type="text"
        placeholder="Serial Number"
        value={serial}
        onChange={(e) => setSerial(e.target.value)}
      />
      <button onClick={registerProduct}>Register</button>

      <h2>Transfer Product</h2>
      <input
        type="text"
        placeholder="Receiver Address"
        value={receiver}
        onChange={(e) => setReceiver(e.target.value)}
      />
      <button onClick={transferProduct}>Transfer</button>

      <h2>Fetch Product Docs</h2>
      <button onClick={fetchDocs}>Get Docs</button>
      <ul>
        {docs.map((d, idx) => (
          <li key={idx}>{d}</li>
        ))}
      </ul>
    </div>
  );
}

export default App;
